function instagramIntegration(){let t=new XMLHttpRequest;const e=document.querySelector("#instagam-cont"),n=document.querySelectorAll(".instagram__user-name"),a=document.querySelectorAll(".instagram__image img"),o=(document.querySelectorAll(".instagram__img__old"),e.getAttribute("data-instagram-post"));t.onload=function(){if(200===t.status){let e=JSON.parse(t.response).posts;if(e)for(let t=0;t<4;t++)try{const o=e[t];n[t].innerHTML="@"+o.user_full_name,a[t].src=o.image}catch(t){console.log(t)}}},t.open("GET",o),t.send()}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",instagramIntegration()):instagramIntegration();