function instagramIntegration(){let t=new XMLHttpRequest,e=document.querySelector("#instagam-cont");var n=e.getAttribute("data-instagram-post");t.onload=function(){if(200===t.status){let n=JSON.parse(t.response).posts;if(n)for(let t=0;t<4;t++){let a=n[t],i=document.createElement("div");i.setAttribute("class","instagram__item");let s=document.createElement("img"),r=a.image;s.setAttribute("src",r),s.setAttribute("alt","instagram post image");let o=document.createElement("p"),m=a.user_full_name;o.setAttribute("class","instagram__user-name"),o.innerHTML="@"+m,i.appendChild(s),i.appendChild(o),e.appendChild(i)}}},t.open("GET",n),t.send()}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",instagramIntegration()):instagramIntegration();