function instagramIntegration(){let e=new XMLHttpRequest;const t=document.querySelector("#instagam-cont"),n=document.querySelectorAll(".instagram__user-name"),a=document.querySelectorAll(".instagram__image img"),o=(document.querySelectorAll(".instagram__img__old"),document.querySelector(".instagram__name-account")),r=t.getAttribute("data-instagram-post");e.onload=function(){if(200===e.status){let t=JSON.parse(e.response).posts;if(t){o.innerHTML="@"+t[0].user_full_name;for(let e=0;e<4;e++)try{const o=t[e];n[e].innerHTML="@"+o.user_full_name,a[e].src=o.image}catch(e){console.log(e)}}}},e.open("GET",r),e.send()}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",instagramIntegration()):instagramIntegration();