function instagramIntegration(){new XMLHttpRequest;const e=document.querySelector("#instagam-cont"),t=document.querySelectorAll(".instagram__user-name"),n=document.querySelectorAll(".instagram__image img"),o=(document.querySelectorAll(".instagram__img__old"),document.querySelector(".instagram__name-account"));if(e){const a=e.getAttribute("data-instagram-post");fetch(a).then((e=>e.json())).catch((e=>{console.log("%c change user token in insta settings","font-weight:800; font-size:22px; color: magenta"),console.log("%c   go to edit page , ","  color: blue"),console.log('%c   finde lield "follow_instagram_feed_id" ,  ',"  color: green"),console.log("%c   and add your id from curator.io ","  color: blue")})).then((e=>{if(e){const a=e.posts;o.innerHTML="@"+a[0].user_full_name;for(let e=0;e<4;e++)try{const o=a[e];t[e].innerHTML="@"+o.user_full_name,n[e].src=o.image}catch(e){console.log(e)}}}))}}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",instagramIntegration()):instagramIntegration();